// Updated function to assign care team with better error handling
export async function assignCareTeam(
  patientId: string, 
  doctorId: string, 
  nutritionistId: string | null, 
  adminId: string
): Promise<any> {
  try {
    console.log("Calling admin_assign_care_team RPC with:", {
      patientId,
      doctorId,
      nutritionistId,
      adminId
    });
    
    const { data, error } = await supabase.rpc('admin_assign_care_team', {
      p_patient_id: patientId,
      p_doctor_id: doctorId,
      p_nutritionist_id: nutritionistId,
      p_admin_id: adminId
    });
    
    console.log("RPC response:", { data, error });
    
    if (error) {
      console.error('Error in admin_assign_care_team RPC:', error);
      throw new Error(error.message || 'Failed to assign care team');
    }
    
    // Handle case where the function returned data but with error status
    if (data && data.error) {
      throw new Error(data.error);
    }
    
    return data;
  } catch (error: any) {
    console.error('Exception in assignCareTeam:', error);
    throw error;
  }
}

